var config = require('config');
var logClient = require('./logClient')();

var apply = function (object, config, defaults) {
    if (defaults) {
        apply(object, defaults);
    }

    if (object && config && typeof config === 'object') {
        var i, j, k;

        for (i in config) {
            object[i] = config[i];
        }
    }

    return object;
};

var logger = logClient.logger;

module.exports = {
    apply : apply,
    logger: logger,
    logClient: logClient,
    log: logger.log,
    logInfo: logger.info,
    logWarn: logger.warn,
    logError: logger.error,
    logi: logger.info,
    logw: logger.warn,
    loge: logger.error,
    config: config
};