var classy = require('classy');
var rs = require('./core');

/**
 * @classdesc Helper class to manipulate arrays.
 * @constructor
 */
var array = classy.define(/** @lends rs.util.array# */{
    forceInstance: true,
    /**
     * Make sure the object, string etc are converted to arrayss
     * @function
     * @param {all} object - Input can be any type: string, number or object.
     */
    ensure: function (arr) {
        if (Array.isArray(arr))
            return arr;
        return [arr];
    },

    exists: function (arr, val) {
        return (arr.indexOf(val) != -1);
    }
})

module.exports = array;